// import YesNoSelect from './YesNoSelect';  // Import the correct component
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles/DealPage.css';  // Ensure you have imported the CSS
import styled from 'styled-components';


const DealPage = ({ match }) => {
    const [deal, setDeal] = useState({});
    const dealId = match.params.id;


    
    useEffect(() => {
        // Fetch deal data
        axios.get(`http://localhost:8080/api/deals/${dealId}`)
            .then(response => {
                setDeal(response.data);
            })
            .catch(error => console.error('Error fetching deal:', error));
    }, [dealId]);

    return (
        <div className="deal-page" style={{backgroundColor: 'lightgrey', padding: '20px'}}>

            {/* Left Pane */}
            <div className="left-pane">
                <div className="summary-section">
                    <h2>Summary</h2>
                    <p>Amount: {deal.amount || '$0'}</p>
                    <p>Owner: {deal.owner || 'N/A'}</p>
                    <p>Status: {deal.status || 'N/A'}</p>
                </div>
                <div className="details-section">
                    <h3>Details</h3>
                    <p>Decision Maker: {deal.decisionMaker || 'N/A'}</p>
                    <p>Job Title: {deal.jobTitle || 'N/A'}</p>
                    <p>Contact Type: {deal.contactType || 'N/A'}</p>
                </div>
            </div>

            {/* Center Area */}
            <div className="center-area">
                <div className="focus-section">
                    <h3>Focus</h3>
                    {/* Focus related content, e.g., upcoming tasks or priorities */}
                    <p>No focus items yet</p>
                </div>
                <div className="history-section">
                    <h3>History</h3>
                    {/* History of the deal */}
                    <p>{deal.history || 'No history available'}</p>
                </div>
            </div>
        </div>
    );
};

export default DealPage;
